mapper.delete:
delete from pe_t_warning
where warning_id in

    #{waringId}

mapper.select:
select

from pe_t_warning


        user_id = #{userId}


        and is_read = #{isRead}


	  AND warn_time >= "${beginDate}" 


        and type = #{type}


        and waring_type = #{waringType}


        and polarity = #{polarity}


        and title like CONCAT('%','${title}','%' )


order by

    warn_time


    publishDate

desc

select count(*) count from pe_t_warning
to_days(warn_time)=to_days(now())
and user_id = #{userId}

SELECT
from pe_t_warning where warning_id in
#{waringId}
